import "./switch.css";

const Switch = () => {
    const [isDarkMode, setIsDarkMode] = useState(false);

    const toggleTheme = () => {
        setIsDarkMode((prevMode) => !prevMode);
    };
    return (
        <label className="switch">
            <input
                type="checkbox"
                checked={isDarkMode}
                onChange={toggleTheme}
            />
            <span className="slider"></span>
        </label>
    );
};
