import React, { useState } from "react";
import "./App.css"; // Ensure you import the CSS file
import ComputerComponent from "./components/Computer";
import HeroCard from "./components/herocard";

function App() {
    const [isDarkMode, setIsDarkMode] = useState(false);

    const toggleTheme = () => {
        setIsDarkMode((prevMode) => !prevMode);
    };

    return (
        //navbar bouton jour/nuit anglais/français + menu déroulant avec lien vers chaque page
        <div className={`container ${isDarkMode ? "dark" : "light"}`}>
            <nav className="navbar">
                <ol>
                    <li>
                        <a href="#Intro" className="navlink">
                            Introduction
                        </a>
                    </li>
                    <li>
                        <a href="#Skills" className="navlink">
                            Skills
                        </a>
                    </li>
                    <li>
                        <a href="#Projects" className="navlink">
                            Projects
                        </a>
                    </li>
                    <li>
                        <a href="#Conctact" className="navlink">
                            Contact
                        </a>
                    </li>
                    <li>
                        {" "}
                        <label className="switch">
                            <input
                                type="checkbox"
                                checked={isDarkMode}
                                onChange={toggleTheme}
                            />
                            <span className="slider"></span>
                        </label>
                    </li>
                </ol>
                <button class="burger-menu" id="burger-menu">
                    <ion-icon class="bars" name="menu-outline"></ion-icon>
                </button>
            </nav>
            <span class="computer-svg">
                <ComputerComponent />
            </span>

            <div className="left-panel"></div>
            <div className="right-panel">
                <div class="description">
                    {" "}
                    <HeroCard />
                </div>
            </div>
        </div>
        //tech utilisée
        //projets
        //contact
    );
}

export default App;
