import React, { useState } from "react";
import "./App.css"; // Ensure you import the CSS file
import ComputerComponent from "./components/Computer";

function App() {
    const [isDarkMode, setIsDarkMode] = useState(false);

    const toggleTheme = () => {
        setIsDarkMode((prevMode) => !prevMode);
    };

    return (
        <div className={`container ${isDarkMode ? "dark" : "light"}`}>
            <span class="computer-svg">
                <ComputerComponent />
            </span>

            <div className="left-panel">
                <h1>Gauche</h1>
            </div>
            <div className="right-panel">

                <h1>Romain Levasseur</h1>
                <p>
                    Excogitatum est super his, ut homines quidam ignoti,
                    vilitate ipsa parum cavendi ad colligendos rumores per
                    Antiochiae latera cuncta destinarentur relaturi quae
                    audirent. hi peragranter et dissimulanter honoratorum
                    circulis adsistendo pervadendoque divites domus egentium
                    habitu quicquid noscere poterant vel audire latenter
                    intromissi per posticas in regiam nuntiabant, id observantes
                    conspiratione concordi, ut fingerent quaedam et cognita
                    duplicarent in peius, laudes vero supprimerent Caesaris,
                    quas invitis conpluribus formido malorum inpendentium
                    exprimebat.
                </p>
            </div>
            <label className="switch">
                <input
                    type="checkbox"
                    checked={isDarkMode}
                    onChange={toggleTheme}
                />
                <span className="slider"></span>
            </label>
        </div>
    );
}

export default App;
