import "./lamp.scss";
import { useState, useEffect } from "react";

const Lamp = () => {
    const [isDarkMode, setIsDarkMode] = useState(false);

    useEffect(() => {
        // Obtenir la valeur du thème depuis localStorage
        const theme = localStorage.getItem("theme");
        setIsDarkMode(theme === "dark");

        // Ajouter un écouteur d'événements pour détecter les changements de thème
        window.addEventListener("storage", handleThemeChange);
        return () => {
            window.removeEventListener("storage", handleThemeChange);
        };
    }, []);

    const handleThemeChange = (event) => {
        setIsDarkMode(event.newValue === "dark");
    };

    return (
        <div className="scene">
            <div className="layer layer-1">
                <div className="desk"></div>
                <div className="plant">
                    <div className="plant-stalk"></div>
                    <div className="plant-leaf"></div>
                    <div className="plant-leaf"></div>
                </div>
                <div className="lamp">
                    <div className="lamp-base"></div>
                    <div className="lamp-mid">
                        <div className="lamp-mid-knob"></div>
                    </div>
                    <div className="lamp-top"></div>
                </div>
                <div
                    className="lamp-effect"
                    style={{ opacity: isDarkMode ? 1 : 0 }}
                >
                    {/* Contenu de la lampe */}
                </div>
                <div className="laptop"></div>
                <div className="book"></div>
                <div className="book-1"></div>
                <div className="book-2"></div>
                <div className="coffee">
                    <div className="coffee-handle"></div>
                    <div className="coffee-brew"></div>
                </div>
                <div className="glass"></div>
            </div>
            <div className="layer layer-2"></div>
            <div className="layer layer-3">
                <div className="painting">
                    <div className="painting-inner"></div>
                </div>
            </div>
        </div>
    );
};

export default Lamp;
