import React, { useEffect } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import "./App.css";
import ComputerPlantComponent from "./components/ComputerPlant";

gsap.registerPlugin(ScrollTrigger);

const App = () => {
    useEffect(() => {
        // Exemple d'animation GSAP avec ScrollTrigger
        gsap.from(".intro-text", {
            opacity: 0,
            duration: 1,
            y: 100,
            scrollTrigger: {
                trigger: ".intro-text",
                start: "top 80%",
            },
        });
    }, []);

    return (
        <div className="App">
            {/* Section de bienvenue */}
            <section className="hero">
                <h1 className="hero-title">Bonjour, je suis [Votre Nom]</h1>
                <p className="hero-subtitle">
                    Développeur web passionné de graphisme
                </p>
            </section>

            {/* Section Introduction */}
            <section className="intro">
                <ComputerPlantComponent />
                <p className="intro-text">Bienvenue dans mon portfolio...</p>
            </section>

            {/* Ajoutez des sections supplémentaires ici */}
        </div>
    );
};

export default App;
